<?php

namespace app\modules\v2\controllers;

use app\modules\v2\components\Utility;
use app\modules\v2\models\ApiResponse;
use app\modules\v2\models\GenerateString;
use app\modules\v2\models\User;
use Aws\Credentials\Credentials;
use Aws\Exception\AwsException;
use Aws\S3\Exception\S3Exception;
use Aws\S3\S3Client;
use Yii;
use yii\helpers\ArrayHelper;
use yii\rest\Controller;
use yii\filters\auth\HttpBearerAuth;


/**
 * Amazon controller
 */
class AwsController extends Controller
{
    public $config = [];
    public $bucketName;

    public function behaviors()
    {
        $behaviors = parent::behaviors();

        //For CORS
        $auth = $behaviors['authenticator'];
        unset($behaviors['authenticator']);
        $behaviors['corsFilter'] = [
            'class' => \yii\filters\Cors::className(),
        ];
        //$behaviors['authenticator'] = $auth;
        $behaviors['authenticator'] = [
            'class' => HttpBearerAuth::className(),
            'except' => ['back-upload', 'upload-file-with-token']
        ];

        return $behaviors;
    }

    public function beforeAction($action)
    {
        $this->bucketName = Yii::$app->params['AwsS3BucketName'];
        $this->config = Utility::AwsS3Config();
        return parent::beforeAction($action);
    }

    public function actionListBucket()
    {
        $s3Client = new S3Client($this->config);
        $buckets = $s3Client->listBuckets();
        return (new ApiResponse)->success($buckets['Buckets'], ApiResponse::SUCCESSFUL);
    }

    public function createBucket($s3Client, $bucketName)
    {
        try {
            $result = $s3Client->createBucket([
                'Bucket' => $bucketName,
            ]);
            return (new ApiResponse)->success($result, ApiResponse::SUCCESSFUL);
        } catch (AwsException $e) {
            return (new ApiResponse)->error($e->getAwsErrorMessage(), ApiResponse::UNABLE_TO_PERFORM_ACTION);

        }
    }

    public function actionCreateBucket($name)
    {
        $s3Client = new S3Client($this->config);
        return (new ApiResponse)->success($this->createBucket($s3Client, $name), ApiResponse::SUCCESSFUL);
    }


    public function actionUploadFile($folder, $base64 = 0, $newname = null)
    {


        try {
            //Create a S3Client
            $result = $this->uploadShared($base64, $folder, $newname);
            return $result;
        } catch (S3Exception $e) {
            return (new ApiResponse)->error($e->getMessage(), ApiResponse::UNABLE_TO_PERFORM_ACTION);

        }
    }

    public function actionUploadFileWithToken($folder, $base64 = 0, $newname = null, $token)
    {
        if (!User::findIdentityByAccessToken($token)) {
            return Yii::$app->params['customError401'];
        }
        try {
            //Create a S3Client
            $result = $this->uploadShared($base64, $folder, $newname);
            $name = isset($_FILES['upload']) ? 'upload' : 'file';
            if ($name == 'upload') {
                return [
                    'uploaded' => 1,
                    'fileName' => $_FILES[$name]['name'],
                    'url' => $result->data['ObjectURL']
                ];
            } else {
                return $result;
            }
        } catch (S3Exception $e) {
            return (new ApiResponse)->error($e->getMessage(), ApiResponse::UNABLE_TO_PERFORM_ACTION);

        }
    }

    private function uploadShared($base64, $folder, $newname)
    {
        $generatedFileName = !empty($newname) ? $newname : GenerateString::widget(['length' => 50]);
        $name = isset($_FILES['upload']) ? 'upload' : 'file';
        if ($base64 == 1) {

            //$fileBase = 'data:application/pdf;base64,';
            $fileBase = $_POST[$name];
            $file_Path = $this->Base64Decode($fileBase, $generatedFileName);
            $fileNameCmps = explode(".", $file_Path);
            $fileExtension = strtolower(end($fileNameCmps));
        } else {

            $fileTmpPath = $_FILES[$name]['tmp_name'];
            $fileName = $_FILES[$name]['name'];
            $fileSize = $_FILES[$name]['size'];
            $fileType = $_FILES[$name]['type'];
            $fileNameCmps = explode(".", $fileName);
            $fileExtension = strtolower(end($fileNameCmps));

            if (!isset($_FILES[$name]) || $_FILES[$name]['error'] !== UPLOAD_ERR_OK) {
                return (new ApiResponse)->error(null, ApiResponse::VALIDATION_ERROR, 'Invalid file');
            }

            $allowedfileExtensions = ['jpg', 'png', 'mp4', 'pdf', 'xls', 'doc', 'docx', 'xlsx', 'ppt', 'pptx'];
            if (in_array($fileExtension, $allowedfileExtensions) === false) {
                return (new ApiResponse)->error(null, ApiResponse::VALIDATION_ERROR, 'Invalid file extension');
            }

            $mbLimit = 100;
            $limit = $mbLimit * 1048576;
            if ($fileSize > $limit) {
                return (new ApiResponse)->error(null, ApiResponse::VALIDATION_ERROR, "Size cannot be more than {$mbLimit} MB");
            }

            $file_Path = $fileTmpPath;
        }


        $bucket = $this->bucketName;

        $key = Yii::$app->params['appFolderLevel'] . $folder . '/' . $generatedFileName . '.' . $fileExtension;
        $s3Client = new S3Client($this->config);

        $result = $s3Client->putObject([
            'Bucket' => $bucket,
            'Key' => $key,
            'SourceFile' => $file_Path,
        ]);
        return (new ApiResponse)->success($result, ApiResponse::SUCCESSFUL);
    }


    public
    function actionVerifyFile($url)
    {
        $key = explode("/", $url, 4);
        $name = $key[3]; //This get the folder/filename.ext
        $s3 = new S3Client($this->config);
        return (new ApiResponse)->success($s3->doesObjectExist($this->bucketName, $name), ApiResponse::SUCCESSFUL);
    }

    public
    function actionDeleteFile($url)
    {
        $key = explode("/", $url, 4);
        $name = $key[3]; //This get the folder/filename.ext
        $s3 = new S3Client($this->config);
        return (new ApiResponse)->success($s3->deleteObject(['Bucket' => $this->bucketName, 'Key' => $name]), ApiResponse::SUCCESSFUL);
    }

    public function actionFileDetail($url, $internal = 0)
    {
        $key = explode("/", $url, 4);
        $name = $key[3]; //This get the folder/filename.ext
        $s3Client = new S3Client($this->config);
        $result = $s3Client->getObject([
            'Bucket' => $this->bucketName,
            'Key' => $name,
            //'SaveAs' => $name
        ]);
        $fileSize = Utility::FormatBytesSize($result['ContentLength']);
        $response = array_merge(ArrayHelper::toArray($result), ['fileSize' => $fileSize]);
        if ($internal == 1)
            return $response;
        else
            return (new ApiResponse)->success($response);
    }


    public
    function Base64Decode($base64_image_string, $output_file_without_extension)
    {

        $splited = explode(',', substr($base64_image_string, 5), 2);
        $mime = $splited[0];
        $data = $splited[1];

        $mime_split_without_base64 = explode(';', $mime, 2);
        $mime_split = explode('/', $mime_split_without_base64[0], 2);
        if (count($mime_split) == 2) {
            $extension = $mime_split[1];
            if ($extension == 'jpeg')
                $extension = 'jpg';
            $output_file_with_extension = $output_file_without_extension . '.' . $extension;
        }
        file_put_contents($output_file_with_extension, base64_decode($data));
        return $output_file_with_extension;

    }


    public function actionBackUpload($folder, $filename, $type = 'thumbnail')
    {
        //$generatedFileName = GenerateString::widget(['length' => 50]);
        if (empty($_FILES['file'])) {
            return (new ApiResponse)->error(null, ApiResponse::VALIDATION_ERROR, 'attachment is missing');
        }

        $fileTmpPath = $_FILES['file']['tmp_name'];
        $fileName = $_FILES['file']['name'];
        $fileSize = $_FILES['file']['size'];
        $fileType = $_FILES['file']['type'];
        $fileNameCmps = explode(".", $fileName);
        $fileExtension = strtolower(end($fileNameCmps));

        if (!isset($_FILES['file']) || $_FILES['file']['error'] !== UPLOAD_ERR_OK) {
            return (new ApiResponse)->error(null, ApiResponse::VALIDATION_ERROR, 'Invalid file');
        }

        $allowedfileExtensions = ['jpg', 'png', 'mp4', 'pdf', 'xls', 'doc', 'docx', 'xlsx', 'ppt', 'pptx'];
        if (in_array($fileExtension, $allowedfileExtensions) === false) {
            return (new ApiResponse)->error(null, ApiResponse::VALIDATION_ERROR, 'Invalid file extension');
        }

        $mbLimit = 10;
        $limit = $mbLimit * 1048576;
        if ($fileSize > $limit) {
            return (new ApiResponse)->error(null, ApiResponse::VALIDATION_ERROR, "Size cannot be more than {$mbLimit} MB");
        }
        $file_Path = $fileTmpPath;

        $bucket = $this->bucketName;

        $key = $folder . "/$type/" . $filename . '.' . $fileExtension;

        try {
            //Create a S3Client
            $s3Client = new S3Client($this->config);
            $result = $s3Client->putObject([
                'Bucket' => $bucket,
                'Key' => $key,
                'SourceFile' => $file_Path,
            ]);

            return (new ApiResponse)->success($result, ApiResponse::SUCCESSFUL);
        } catch (S3Exception $e) {
            return (new ApiResponse)->error($e->getMessage(), ApiResponse::UNABLE_TO_PERFORM_ACTION);

        }
    }
}

